mapper.insert:
insert into
	accounts (
		id
		,email
		,pwhash
		,dob
	) values (
		(
			select id
			from (
				select
					trunc(dbms_random.value(1000000000000000,10000000000000000)) as id
				from dual
				connect by level <= 1000
				minus (select id from accounts)
			)
			where rownum = 1
		)
		,#{email}
		,#{pwhash}
		,#{dob}				
	)

mapper.select:
select count(id)
from accounts
where email = #{email}
