mapper.insert:
insert into reportpost values( post_seq.nextval, #{user_id}, #{title}, #{content}, sysdate, 0, 0,
#{originalFilename}, #{savedFilename}
null, null
)

mapper.update:
UPDATE reportpost SET title = #{title}, content = #{content},
originalFilename = #{originalFilename}, savedFilename = #{savedFilename}
WHERE post_no = #{post_no} and user_id = #{user_id}

update tolike
set like_check = like_check +1
where user_id = #{user_id} and post_no = #{post_no}

update reportpost
set like_cnt = like_cnt + 1
where post_no = #{post_no}

update reportpost
set like_cnt = like_cnt -1
where post_no = #{post_no}

UPDATE tolike
SET like_check = 0
WHERE user_id = #{user_id} and post_no = #{post_no}

mapper.select:
SELECT COUNT(like_no) as cnt
FROM tolike
WHERE post_no = #{post_no} AND user_id = #{user_id}
